(16, 410, 'Funding - Impact Investing - Mulago 4 of 8', 'Kevin Starr of Mulago helps you specify the sequence of behaviors you must change to impact the desired outcome.\r\n\r\nhttps://youtu.be/JWE_8SXYgb4', '18', '2', 'July 14, 2018', 'July 14, 2030', '1', '', '', '', '', 1, '2018-07-14 18:37:37', '2018-07-14 18:47:59'),
(17, 410, 'Funding - Impact Investing - Mulago 5 of 8', 'Kevin Starr of Mulago helps you clarify the interventions you must execute to change the sequence of behaviors required to impact the outcome.\r\n\r\nhttps://youtu.be/oe6YYibp-xA', '18', '2', 'July 14, 2018', 'July 14, 2030', '1', '', '', '', '', 1, '2018-07-14 18:46:05', '2018-07-14 19:01:34'),
(18, 410, 'Funding - Impact Investing - Mulago 6 of 8', 'Kevin Starr of Mulago helps you define the systematic and efficient business model required to drive the interventions required to impact the outcome.\r\n\r\nhttps://youtu.be/oBEt61JX1eo\r\n', '18', '2', 'July 14, 2018', 'July 14, 2030', '1', '', '', '', '', 1, '2018-07-14 18:53:07', '2018-07-14 18:59:45'),
(19, 410, 'Funding - Impact Investing - Mulago 7 of 8', 'Kevin Starr of Mulago helps you clarify who will operate your business model and who will pay for your solution that impacts the desired outcome.\r\n\r\nhttps://youtu.be/_rcmUF5nBFU', '18', '2', 'July 14, 2018', 'July 14, 2030', '1', '', '', '', '', 1, '2018-07-14 18:56:07', '2018-07-14 19:01:00'),
