								$finalStateList[] = $StateList;
							}
						}
					$CountryList['state']=$finalStateList;				
					
					$finalCountryList[] = $CountryList;
				}
			}
			if(!empty($finalCountryList)){
				
            	$result['code'] = 200;
				$result['country'] = $finalCountryList;
				
				echo json_encode($result);
				
            }else{
			 	  
				$result['code'] = 404;
				$result['message'] = "No Countries Found";
				
			 	echo json_encode($result);
				
			}
	}



	
