	            }else{
	            	$result['code']=404;
					$result['message']= 'Could not find board member.';
					echo json_encode($result);
	            }        
            }else{
            	$result['code']=404;
				$result['message']= 'Could not like board member.';
				echo json_encode($result);
            }
    }

