						$this->InformationDislikes->delete($entity);
					}

	                $this->loadModel('InformationLikes');
					$likefunds = $this->InformationLikes->newEntity(); 

					
	                $likefunds = $this->InformationLikes->patchEntity($likefunds, $this->request->data);

